(function () {

    var lottery = {
        run: function run() {
            $('#lottery-bg').addClass("fn-run");
        },
        stop: function stop(id) {
            $('#lottery-bg').removeClass("fn-run").addClass('rotateInStop-' + id);
        }
    };

    var app = {
        init: function init() {

            this.even();
        },
        even: function even() {

            $('#lottery-btn').on('click', function () {
                lottery.run();

                var id = Math.random(0, 5);
                setTimeout(function () {
                    lottery.stop(id);
                }, 3000);
            });
        }
    };

    app.init();
})();